mapper.select:
select * from t_user
AND brith >= STR_TO_DATE(#{condition.beginDate},'%Y-%m-%d') AND brith <= STR_TO_DATE(#{condition.endDate},'%Y-%m-%d') AND school like CONCAT('%',#{condition.schoolName},'%') AND major like CONCAT('%',#{condition.specialty},'%')
limit #{page},#{limit}

mapper.update:
update t_station
title = #{station.title}, region = #{station.region}, experience = #{station.experience}, money = #{station.money}, worktime = #{station.worktime}, description = #{station.description}, welfare = #{station.welfare}, number = #{station.number},
where id = #{station.id}

update t_company
name = #{company.name}, address = #{company.address}, tel = #{company.tel}, introduce = #{company.introduce}, business = #{company.business},
id = #{company.id}

update t_jobcontain

    one = #{value}
    two = #{value}
    three = #{value}
    four = #{value}

where id = #{id}
