mapper.select:
select u.id,u.account,u.pwd,u.name,u.sex,u.age,u.tel,u.address,u.jobstate,c.birthday,c.specialty,s.name as schoolName from t_user u inner join t_CV c on u.id = c.userid inner join t_school s ON s.id = c.schoolid
AND c.birthday >= STR_TO_DATE(#{condition.beginDate},'%Y-%m-%d') AND c.birthday <= STR_TO_DATE(#{condition.endDate},'%Y-%m-%d') AND s.schoolName like CONCAT('%',#{condition.schoolName},'%') AND c.specialty like CONCAT('%',#{condition.specialty},'%')
limit #{page},#{limit}

select id,value,type,name,state from t_param  where state='true'


    and name like concat('%',#{name},'%')


    and type=#{type}

limit #{start},#{pageSize}

select count(*) from t_param  where state='true'


    and name like concat('%',#{name},'%')


    and type=#{type}
