mapper.select:
select m1.id,p.name as scope,m1.menuname,(case when m2.menuname is null then '无' else m2.menuname
end) as upmenu from t_knowmenu m1 left join t_knowmenu m2
on m1.pid=m2.id left join t_param p on p.id=m1.scope where 1=1

     and m1.menuname like concat('%',#{title},'%')


     and p.name=#{scope}

 limit #{page},#{limit}

select count(*) from( select m1.id,p.name as scope,m1.menuname,(case when m2.menuname is null then '无' else m2.menuname end) as upmenu from t_knowmenu m1 left join t_knowmenu m2 on m1.pid=m2.id left join t_param p on p.id=m1.scope and p.name='tt') c where 1=1
and m1.menuname like concat('%',#{title},'%')
and p.name=#{scope}
